sys_script_client.script:
function onLoad() {
	// Call API from freecurrencyconverter
	var ga = new GlideAjax('FreeCurrencyConverter');
	ga.addParam('sysparm_name', 'getRate');
	ga.addParam('sysparm_local_currency', g_form.getDisplayBox('local_currency').value);
	ga.getXML(responseHandler);

	// Attachment manipulate
// 	if (g_user.hasRole('x_245925_dxc_docs.DXC Employee') && (g_form.getValue('state') == 'Declined' || g_form.getValue('state') == 'Cancelled')) {
// 		g_form.disableAttachments();
// 	} else {
// 		g_form.enableAttachments();
// 	}
}

function responseHandler(response){
	var rate = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('gbp', rate);

}
