sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var local_currency = g_form.getReference('local_currency', getGBP);

	function getGBP(local_currency){
		var ga = new GlideAjax('FreeCurrencyConverter');
		ga.addParam('sysparm_name', 'getRate');
		ga.addParam('sysparm_local_currency', local_currency.code);
		ga.getXML(responseHandler);

		function responseHandler(response){
			var rate = response.responseXML.documentElement.getAttribute("answer");
			g_form.setValue('gbp', rate);
		}
	}
}
