sys_script_include.script:
var FreeCurrencyConverter = Class.create();
FreeCurrencyConverter.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {	
	getRate : function(){
		// Invoke REST Message to get currency rate from web service API
		var cur_pair = this.getParameter('sysparm_local_currency') + '_GBP';
		try {
			var r = new sn_ws.RESTMessageV2('x_245925_dxc_docs.Free Currency Converter', 'Default GET');
			r.setStringParameterNoEscape('currency_pair', cur_pair);

			//override authentication profile
			//authentication type ='basic'/ 'oauth2'
			//r.setAuthentication(authentication type, profile name);

			//set a MID server name if one wants to run the message on MID
			//r.setMIDServer('MY_MID_SERVER');

			//if the message is configured to communicate through ECC queue, either
			//by setting a MID server or calling executeAsync, one needs to set skip_sensor
			//to true. Otherwise, one may get an intermittent error that the response body is null
			//r.setEccParameter('skip_sensor', true);

			var response = r.executeAsync();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();

			var obj = JSON.parse(responseBody);
			return '' + obj[cur_pair]['val'];
		}
		catch(ex) {
			var message = ex.toString();
			gs.addErrorMessage(message);
		}
	},

	type: 'FreeCurrencyConverter'
});
